package controller;
import container.*;
import java.util.ArrayList;
public class Controller {

	public void damagePump(ArrayList<Pump> pumps) {
		for(Pump p:pumps) {
			if(turnCount==p.getRandomDamageValue() && !p.isDamaged()) {
				p.setisDamaged(true);
			}
		}
	}

	public void waterFlow(Pipe p1, Pump pu, Pipe p2)
	{
		p1.eval();
		p2.eval();
		p1.makeHistory();
		p2.makeHistory();
	}
}
